import {
    START_SESS,
    END_SESS,
    SET_CURRENT_PROFILE_TAB,
} from '../actions/types';

const initialState = {
    authenticated: false,
    currentProfileTab: 'details', //detaisl | form
};

export default function (state = initialState, { type, payload }) {
    switch (type) {
        case START_SESS:
            return {
                ...state,
                authenticated: true,
            };
        case END_SESS:
            return {
                ...state,
                authenticated: false,
            };
        case SET_CURRENT_PROFILE_TAB:
            return {
                ...state,
                currentProfileTab: payload,
            };
        default:
            return state;
    }
}
