import React, { useState, useEffect, Fragment } from 'react';
import { v4 as uuidv4 } from 'uuid';
import FormInput from './FormInput';
import FormErrorsDisplay from './FormErrorsDisplay';

const createDOMPurify = require('dompurify');
const { JSDOM } = require('jsdom');
const window = new JSDOM('').window;
const DOMPurify = createDOMPurify(window);

export default function AddCustomFields({
    addCustomToFieldsFormState,
    maxPosition,
}) {
    const [formData, setFormData] = useState({
        customFieldLabel: '',
        customFieldValue: '',
        customFieldPosition: null,
        errors: [],
    });
    const handleAdd = async () => {
        const {
            customFieldLabel,
            customFieldValue,
            customFieldPosition,
        } = formData;
        if (!customFieldLabel.trim()) {
            alert('no label');
            return;
        }
        if (!customFieldValue.trim()) {
            alert('no value');
            return;
        }

        //create customised field
        const customField = {
            _id: uuidv4(),
            label: DOMPurify.sanitize(customFieldLabel),
            value: customFieldValue.trim(),
            position: customFieldPosition || ++maxPosition,
            addToInvoice: true,
            custom: true,
            inputType: 'text',
        };
        await addCustomToFieldsFormState(customField);
        //reset state
        setFormData({
            customFieldLabel: '',
            customFieldValue: '',
            customFieldPosition: null,
            errors: [],
        });
        //set last option as default
        document
            .getElementById('customFieldPosition')
            .lastElementChild.setAttribute('selected', true);
    };
    const getSelectedPosition = () => {
        const el = document.getElementById('customFieldPosition');
        setFormData({
            ...formData,
            customFieldPosition: el.options[el.selectedIndex].value,
        });
    };
    useEffect(() =>
        document
            .getElementById('customFieldPosition')
            .lastElementChild.setAttribute('selected', true)
    );
    return (
        <fieldset className='add-custom-fields'>
            <legend>
                <b>Add custom fields to the form.</b>
            </legend>
            <FormInput
                form={{ formData, setFormData }}
                name='customFieldLabel'
                size='auto'
            >
                Description*
            </FormInput>
            <FormInput
                form={{ formData, setFormData }}
                name='customFieldValue'
                size='auto'
            >
                Value*
            </FormInput>

            <label htmlFor='customFieldPosition'>Choose a position:</label>
            <select id='customFieldPosition' onChange={getSelectedPosition}>
                {[...Array(++maxPosition).keys()].map((key) => (
                    <option value={key} key={'position-of-custom-field-' + key}>
                        {key + 1}
                    </option>
                ))}
            </select>

            {formData.errors.length > 0 && (
                <FormErrorsDisplay errors={formData.errors} label='' />
            )}
            <button
                className='btn btn--grey'
                onMouseDown={(e) => e.preventDefault()}
                onClick={handleAdd}
            >
                Add Item
            </button>
        </fieldset>
    );
}
