import axios from 'axios';
const jsonEqual = (a, b) => {
    return JSON.stringify(a) === JSON.stringify(b);
};

export default async function onUnload(appState) {
    var back = true;

    window.onbeforeunload = function (e) {
        if (back == true) return 'Are you sure to exit?';
    };

    const config = {
        headers: {
            'Content-Type': 'application/json',
        },
    };
    const { profile, companies } = appState;
    const profileDB = await axios.get('/api/profile');
    console.log('unloading....');
    jsonEqual(profile, profileDB) ||
        (await axios.post('/api/profile', JSON.stringify(profile), config));

    const companiesDB = await axios.get('/api/companies');
}
