export const getInputObjById = (arr, id) => {
    return arr.find((input) => input._id === id);
};

export const getInputValueByLabel = (arr, l) => {
    return arr.find((input) => input.label === l);
};

//arr -> obj with arr ( obj with value property  that position in arr chnages dinamicaly )
