import React, { useState } from 'react';
import PropTypes from 'prop-types';
import arrowIcon from '../../../imgs/icons/arrow.png';
import {
    moveItemUpOrDown,
    toggleIncludedInInvoice,
} from '../utils/displayTableFun';

export default function TasksDisplayTable({ tasks, updateState }) {
    const [tableState, setTableState] = useState(tasks);
    console.log(tableState);
    return (
        <table className='details-table'>
            <caption>
                <b>Current tasks list.</b>
                <p>
                    Use arrows to move items up and down the list. By default
                    all items will be included in a new invoice form and remove
                    from the list once the invoice is created.
                </p>
            </caption>
            <thead>
                <tr className='sr-only'>
                    <th scope='col'>Task description</th>
                    <th scope='col'>Quantity</th>
                    <th scope='col'>Rate</th>
                    <th scope='col'>Amount</th>
                    <th scope='col'>Add task to a new invoice</th>
                    <th scope='col'>Move Item Up</th>
                    <th scope='col'>Move Item Down</th>
                    <th scope='col'>Update Task</th>
                    <th scope='col'>Delete Task</th>
                </tr>
            </thead>
            <tbody>
                {tableState.length === 0 && (
                    <tr key={no - tasks}>
                        <td className='tile' colSpan='9'>
                            You have currently none saved tasks.
                        </td>
                    </tr>
                )}
                {tableState.length > 0 &&
                    tableState.map((item, index, arr) => {
                        if (item.value && item.inputType !== 'textarea')
                            return (
                                <tr key={item._id} data-details-index={index}>
                                    <th scope='row'>{item.label}:</th>
                                    <td className='td__value'>{item.value}</td>
                                    <td>
                                        <button
                                            onMouseDown={(e) =>
                                                e.preventDefault()
                                            }
                                            title={
                                                item.addToInvoice
                                                    ? 'Item will be included in the new invoice.'
                                                    : ' Item will NOT be included in the new invoice.'
                                            }
                                            onClick={() => {
                                                toggleIncludedInInvoice(
                                                    index,
                                                    tableState,
                                                    setTableState,
                                                    updateState
                                                );
                                            }}
                                        >
                                            {item.addToInvoice ? (
                                                <span>&#43;</span>
                                            ) : (
                                                <span>&#45;</span>
                                            )}
                                        </button>
                                    </td>
                                    <td>
                                        {index !== 0 && (
                                            <button
                                                onMouseDown={(e) =>
                                                    e.preventDefault()
                                                }
                                                className='arrow-up'
                                                title='Move item up'
                                                onClick={(e) =>
                                                    moveItemUpOrDown(
                                                        e,
                                                        'up',
                                                        tableState,
                                                        setTableState,
                                                        updateState
                                                    )
                                                }
                                            >
                                                <img
                                                    src={arrowIcon}
                                                    alt='move item up'
                                                />
                                            </button>
                                        )}
                                    </td>
                                    <td className='td__last'>
                                        {/* last td of last tr disabled with css */}
                                        <button
                                            onMouseDown={(e) =>
                                                e.preventDefault()
                                            }
                                            className='arrow-down'
                                            title='Move item down'
                                            onClick={(e) =>
                                                moveItemUpOrDown(
                                                    e,
                                                    'down',
                                                    tableState,
                                                    setTableState,
                                                    updateState
                                                )
                                            }
                                        >
                                            <img
                                                src={arrowIcon}
                                                alt='move item up'
                                            />
                                        </button>
                                    </td>
                                </tr>
                            );
                    })}
            </tbody>
        </table>
    );
}
TasksDisplayTable.propTypes = {
    tasks: PropTypes.array,
};
