import axios from 'axios';
import { GET_USER, END_SESS, START_SESS } from './types';
import { endSession } from './session';

export const getCurrentUser = () => async (dispatch, getState) => {
    try {
        const res = await axios.get('/api/user');
        dispatch({
            type: GET_USER,
            user: res.data,
        });
        dispatch({ type: START_SESS });
    } catch (err) {
        console.log(err);
        // if (
        //     err.response.data.msg === 'AuthError' &&
        //     getState().session.authenticated
        // ) {
        //     dispatch(
        //         endSession('Your session has expired. Please sign back in.')
        //     );
        //     return;
        // }
        dispatch({ type: END_SESS });
    }
};
