import React, { useEffect, Fragment } from 'react';
import PropTypes from 'prop-types';
import { Redirect, useParams } from 'react-router-dom';
import { connect } from 'react-redux';
import Page from '../../components/page/Page';
import CompanySubmenu from './CompanySubmenu';
import { companyInitDetails } from '../addCompany/companyInitDetails';
import CustomBuiltForm from '../../components/form/forms/CustomBuiltForm';
import { getInputValueByLabel } from '../../components/form/utils/customFormQueries';
import { getAllCompanies } from '../../redux/actions/companies';

export const Company = ({ companies, currentCompanyTab, getAllCompanies }) => {
    let formData;
    if (company) {
        const { details } = company;
        const companyName = getInputValueByLabel(details, 'Name');

        formData = {
            details,
            http: '/api/companies/',
            url: '/dashboard/companies/',
            cb: getAllCompanies,
            msg: `${companyName} has been updated successfully.`,
        };
    }

    useEffect(() => {
        window.scrollTo(0, 0);
        let { id } = useParams();
        const company = companies.find((c) => c._id === id);
    });
    return (
        <Page>
            {!company && <Redirect to='dashboard/companies' />}
            {company && (
                <Fragment>
                    <CompanySubmenu />
                    {currentCompanyTab === 'tasks' && (
                        <div className='tile'>tasks</div>
                    )}
                    {currentCompanyTab === 'invoices' && (
                        <div className='tile'>invoices</div>
                    )}
                    {currentCompanyTab === 'details' && (
                        <div className='tile'>details</div>
                    )}
                    {currentCompanyTab === 'update' && (
                        <CustomBuiltForm
                            data={formData || companyInitDetails}
                        />
                    )}
                </Fragment>
            )}
        </Page>
    );
};
Company.propTypes = {};
const mapStateToProps = (state) => ({
    companies: state.companies.companies,
    currentCompanyTab: state.session.currentCompanyTab,
    getAllCompanies: PropTypes.func,
});

const mapDispatchToProps = {
    getAllCompanies,
};

export default connect(mapStateToProps, mapDispatchToProps)(Company);
