import { SET_INVOICE_INIT_STATE } from '../actions/types';

const initialState = {
    // _id: null,
    // savedAs: null,
    // createdAt: null,
    color: 'orange',
    notes: null,
    profile: [],
    company: [],
    tasks: [],
};

export default function (state = initialState, { type, payload }) {
    switch (type) {
        case SET_INVOICE_INIT_STATE:
            return { ...payload };
        // case UPDATE_INVOICE:
        //     return { ...state, details: [...payload] };
        default:
            return state;
    }
}
