import React, { useState, useEffect } from 'react';
import './Calendar.scss';
import { DAYS, DAYS_LEAP, DAYS_OF_THE_WEEK, MONTHS, THIS_YEAR } from './dates';

export function Calendar() {
    const today = new Date();
    const [date, setDate] = useState(today);
    const [day, setDay] = useState(date.getDate());
    const [month, setMonth] = useState(date.getMonth());
    const [year, setYear] = useState(date.getFullYear());
    const [startDay, setStartDay] = useState(getStartDayOfMonth(date));

    useEffect(() => {
        setDay(date.getDate());
        setMonth(date.getMonth());
        setYear(date.getFullYear());
        setStartDay(getStartDayOfMonth(date));
    }, [date]);

    function getStartDayOfMonth(date) {
        return new Date(date.getFullYear(), date.getMonth(), 1).getDay();
    }

    function isLeapYear(year) {
        return (year % 4 === 0 && year % 100 !== 0) || year % 400 === 0;
    }

    const days = isLeapYear(THIS_YEAR) ? DAYS_LEAP : DAYS;

    return (
        <article className='calenadar'>
            <table>
                <div>
                    <button
                        onClick={() => setDate(new Date(year, month - 1, day))}
                    >
                        Prev
                    </button>
                    <div>
                        {MONTHS[month]} {year}
                    </div>
                    <button
                        onClick={() => setDate(new Date(year, month + 1, day))}
                    >
                        Next
                    </button>
                </div>
                <thead>
                    <tr>
                        {DAYS_OF_THE_WEEK.map((d) => (
                            <th scope='col' key={d} className='day__name'>
                                <b>{d}</b>
                            </th>
                        ))}
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        {Array(days[month] + (startDay - 1))
                            .fill(null)
                            .map((_, index, arr) => {
                                // console.log(index);
                                const d = index - (startDay - 2);
                                let td = 0;
                                if (td === 0 || td % 7 === 0) {
                                    console.log(td);
                                }
                                return (
                                    <td
                                        className='day'
                                        key={index}
                                        // isToday={d === today.getDate()}
                                        // isSelected={d === day}
                                        onClick={() =>
                                            setDate(new Date(year, month, d))
                                        }
                                    >
                                        {d > 0 ? d : ''}
                                    </td>
                                );
                            })}
                    </tr>
                </tbody>
            </table>
        </article>
    );
}
