import React, { useState, useEffect, Fragment } from 'react';
import axios from 'axios';
import { v4 as uuidv4 } from 'uuid';
import FormInput from '../components/FormInput';
import FormErrorsDisplay from '../components/FormErrorsDisplay';
import { formErrorsStyling } from '../utils/formFuns';
import {
    getAllTasks,
    updateCompanyArr,
} from '../../../redux/actions/companies';

export default function TaskForm({ companyID, updateTasksArr, update }) {
    const [formData, setFormData] = useState({
        description: '',
        qty: '',
        rate: '',
        tax: '',
        errors: [],
    });
    const handleAdd = async (e) => {
        e.preventDefault();
        const { description, qty, rate, tax, amount } = formData;
        const errors = [];

        const regExp = /^[^0-9]*[0-9]{1,3}(,?[0-9]{3})*(\.[0-9]{1,2})?[^0-9]*$/;
        console.log(regExp.test(formData.rate));
        console.log(formData.rate.replace(/[^0-9,.]/g, ''));
        const regExpPercentage = /^([0-9]{1,2}(\.[0-9]{1,2})?|100)\s?%?$/;
        console.log(regExpPercentage.test(formData.tax));
        console.log(formData.tax.replace(/[^0-9,.]/g, ''));

        if (!description.trim()) {
            const error = {
                param: 'description',
                msg: 'Please describe the task.',
            };
            errors.push(error);
        }

        if (errors.length > 0) {
            setFormData({
                ...formData,
                errors,
            });
            return;
        }

        try {
            const config = {
                headers: {
                    'Content-Type': 'application/json',
                },
            };
            //create task
            const task = {
                _id: uuidv4(),
                description,
                qty: qty || 'N/A',
                rate: rate || 'N/A',
                tax: tax || '0%',
                // amountGross, //before tax
                // amountNet,
                addToInvoice: true,
                createdAt: new Date(),
            };
            let body = { task };
            if (update) body.update = update;
            body = JSON.stringify(body);
            console.log(companyID);
            await axios.post(`/api/companies/tasks/${companyID}`, body, config);
            //update company tasks
            updateCompanyArr();
            //reset state
            setFormData({
                description: '',
                qty: '',
                rate: '',
                amount: '',
                errors: [],
            });
        } catch (err) {
            console.log(err);
        }
    };

    useEffect(() => {
        formErrorsStyling(formData.errors);
    }, [formData.errors]);
    return (
        <form className='task-form' onSubmit={handleAdd}>
            <fieldset>
                <legend>
                    <b>Add a new task to bill for. {companyID}</b>
                    <p>
                        <small> *Required</small>
                    </p>
                </legend>
                <div className='grid-3-cls'>
                    <span className='col-span-3'>
                        <FormInput
                            form={{ formData, setFormData }}
                            name='description'
                            size='auto'
                        >
                            Description*
                        </FormInput>
                    </span>
                    <span>
                        <FormInput
                            form={{ formData, setFormData }}
                            name='qty'
                            size='auto'
                        >
                            Qty (eg. 1, N/A, Free)
                        </FormInput>
                    </span>
                    <span>
                        {' '}
                        <FormInput
                            form={{ formData, setFormData }}
                            name='rate'
                            size='auto'
                        >
                            Rate (eg. Â£11, N/A, Free)
                        </FormInput>
                    </span>
                    <span>
                        <FormInput
                            form={{ formData, setFormData }}
                            name='tax'
                            size='auto'
                        >
                            Tax (eg. 10%, 10)
                        </FormInput>
                    </span>
                </div>
                <button
                    className='btn btn--info'
                    onMouseDown={(e) => e.preventDefault()}
                    onClick={handleAdd}
                >
                    Add Item
                </button>
            </fieldset>
            {formData.errors.length > 0 && (
                <FormErrorsDisplay
                    errors={formData.errors}
                    label='Add custom field form'
                />
            )}
        </form>
    );
}
