import React, { useState, useEffect, Fragment } from 'react';
import './InvoiceDoc.scss';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import settingsIcon from '../../../../imgs/icons/cogs.png';
import updateIcon from '../../../../imgs/icons/updateIcon.png';

const InvoiceDocHeader = ({ profile }) => {
    const [settings, setSettings] = useState(false);
    const profileDetails = profile.details.map((input) => {
        if (input.label === 'Name' && input.addToInvoice) {
            return (
                <li key={input._id}>
                    <b>{input.value}</b>
                </li>
            );
        } else if (input.addToInvoice) {
            return <li key={input._id}>{input.value}</li>;
        }
    });

    const colors = [
        ['000', 'black'],
        ['5F7C8A', 'grey'],
        ['785447', 'brown'],
        ['FF5623', 'bright orange'],
        ['EB9346', 'orange'],
        ['FFC108', 'yellow'],
        ['CDDD39', 'yellow-green'],
        ['8BC34A', 'light green'],
        ['4BAF50', 'green'],
        ['009688', 'dark green'],
        ['01BCD5', 'see blue'],
        ['03A9F4', 'blue'],
        ['2296F3', 'dark blue'],
        ['3F50B6', 'dark purple'],
        ['673BB7', 'purple'],
        ['9C27B0', 'purple-pink'],
        ['E91F63', 'pink'],
        ['D5133A', 'dsrk pink'],
        ['F44337', 'red'],
        ['C91020', 'crimson'],
    ];
    const colorPicker = colors.map((color) => (
        <li
            key={'color-picker-' + color[0]}
            style={{ backgroundColor: `#${color[0]}` }}
        >
            .<span className='sr-only'>color[1]</span>
        </li>
    ));
    return (
        <Fragment>
            {settings && (
                <section className='tile'>
                    <h3 id='color-picker'>Choose invoice color theme.</h3>
                    <ul
                        aria-labelledby='color-picker'
                        className='color-pickere'
                    >
                        {colorPicker}
                    </ul>
                    <button
                        className='close'
                        onClick={() => setSettings(false)}
                    >
                        &times;
                    </button>
                </section>
            )}
            <header>
                <button
                    className='icon_iSettings'
                    title='Change invoice settings'
                    onClick={() => setSettings(true)}
                >
                    <img src={settingsIcon} alt='Invoice settings' />
                </button>
                <div>
                    <h1>invoice</h1>
                </div>
                <div className='txt--right'>
                    <button
                        className='icon_iProfile'
                        title='Change Profile details'
                    >
                        <img src={updateIcon} alt='Change Profile details' />
                    </button>
                    <ul>{profileDetails}</ul>
                </div>
            </header>
        </Fragment>
    );
};

InvoiceDocHeader.propTypes = {};
const mapStateToProps = (state) => ({
    profile: state.profile,
});

export default connect(mapStateToProps)(InvoiceDocHeader);
