import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { withRouter } from 'react-router-dom';
import axios from 'axios';
import { connect } from 'react-redux';
import { getAllCompanies } from '../../../redux/actions/companies';
import FormInput from '../../../components/form/components/FormInput';

export const NewInvoiceSubmit = ({ company, invoice, history }) => {
    const downloadInvoice = () => {
        const invoice = document.getElementById('invoice').innerHTML;
        const pdf = window.open();
        pdf.document.write('<html><head>');
        pdf.document.write(
            `<link rel="stylesheet" type="text/css" href="${window.location.origin}/invoice.css"/>`
        );
        pdf.document.write(
            ` <link
            href="https://fonts.googleapis.com/css?family=Audiowide|Inter:400,600&display=swap"
            rel="stylesheet"
        />`
        );
        pdf.document.write('</head><body>');
        pdf.document.write(invoice);
        pdf.document.write('</body></link>');
        pdf.document.close();
        setTimeout(function () {
            pdf.print();
        }, 100);
    };
    const [saveAs, setSaveAs] = useState(false);
    const show_form = async () => {
        await setSaveAs(true);
        document.querySelector('.form__save-as input').focus();
    };
    const [formData, setFormData] = useState({
        saveAs: invoice.saved_as,
        errors: [],
    });
    const saveInvoice = async () => {
        console.log(invoice);
        //save invoice state

        try {
            const config = {
                headers: {
                    'Content-Type': 'application/json',
                },
            };
            //create task
            const task = {
                description,
                qty: qty || 'N/A',
                rate: rate || 'N/A',
                tax: tax || '0%',
                amount,
            };
            task._id = currentTask ? currentTask._id : uuidv4();
            task.addToInvoice = currentTask ? currentTask.addToInvoice : true;
            task.createdAt = currentTask ? currentTask.createdAt : new Date();

            await axios.post(
                `/api/companies/task/${currentCompany}`,
                JSON.stringify(task),
                config
            );
            getAllCompanies();
            // setCurrentTask(null);
        } catch (err) {
            console.log('Invoice saving err:', err);
        }
        //delete ONLY the tasks added to invoice form tasks list
        //clear invoice doc
        setSaveAs(false);
        history.push(`/dashboard/companies/${company._id}?tab=invoices`);
    };

    return (
        <section>
            <h3 className='sr-only'>Save or download invoice form.</h3>
            {saveAs && (
                <form
                    className='form__save-as'
                    onSubmit={(e) => e.preventDefault()}
                >
                    <FormInput
                        form={{ formData, setFormData }}
                        name='saveAs'
                        size='sm'
                    >
                        <b>Save as: </b>
                    </FormInput>
                    <button
                        className='btn btn--info btn--sibling'
                        onClick={saveInvoice}
                    >
                        {' '}
                        Save
                    </button>
                    <button className='btn' onClick={() => setSaveAs(false)}>
                        {' '}
                        Cancel
                    </button>
                </form>
            )}

            {!saveAs && (
                <button
                    className='btn btn--sibling'
                    onClick={show_form}
                    id='save-as'
                >
                    Save As
                </button>
            )}
            {!saveAs && (
                <button className='btn btn--info' onClick={downloadInvoice}>
                    Download*
                </button>
            )}
            <p>
                *{' '}
                <small>
                    You can either print or save the invoice in pdf format.{' '}
                    <br />
                    We recommend using Chrome browser for the best experience.
                    On clicking this button follow your browsers specification
                    on how to handle the process.
                </small>
            </p>
        </section>
    );
};

NewInvoiceSubmit.propTypes = {
    company: PropTypes.object,
    invoice: PropTypes.object,
    getAllCompanies: PropTypes.func,
};

const mapStateToProps = (state) => ({
    company: state.companies.find(
        (c) => c._id === state.session.currentCompany
    ),
    invoice: state.invoice,
});

const mapDispatchToProps = {
    getAllCompanies,
};

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(withRouter(NewInvoiceSubmit));
