import {
    SET_ALERT,
    REMOVE_ALERT,
    SET_ERROR,
    REMOVE_ERROR,
    CLEAR_ALL_ERRORS,
} from '../actions/types';

const initialState = {
    alerts: null,
    errors: null,
};

export default function (state = initialState, { type, payload }) {
    switch (type) {
        case SET_ALERT:
            return [...state, payload];
        case REMOVE_ALERT:
            return state.filter((alert) => alert.id !== payload);

        default:
            return state;
    }
}
