import React, { useState, useEffect } from 'react';
import './Form.scss';

export default function Form({ fields, url }) {
    const [formState, setFormState] = useState(fields);

    const onSubmit = (e) => {
        e.preventDefault();
    };

    return (
        <form onSubmit={onSubmit} className='form form__submit'>
            <fieldset className='tile'>
                <legend class='sr-only'>
                    Use this form to create or update the details of this page.
                </legend>
                {formState.map((field) => (
                    <div>{field.desc}</div>
                ))}
                <button className='btn btn--info'>Submit</button>
            </fieldset>
            <div>
                <fieldset className='tile'>
                    <legend>Add custom fields to your form.</legend>
                </fieldset>
                <fieldset className='tile'>
                    <legend>Remove custom fields from the form.</legend>
                </fieldset>
            </div>
        </form>
    );
}
