import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

const InvoiceDoc = ({ profile }) => {
    console.log(window.location.origin);
    const getInvoice = () => {
        const invoice = document.getElementById('invoice').innerHTML;
        const pdf = window.open('', 'Invoice PDF', 'height=400, width=800');
        pdf.document.write('<html><head>');
        pdf.document.write(
            `<link rel="" type="text/css" href="${window.location.origin}/invoice.css"/>`
        );
        pdf.document.write('</head><body>');
        pdf.document.write(invoice);
        pdf.document.write('</body></link>');
        pdf.document.close();
        pdf.print();
    };
    return (
        <article id='invoice'>
            <header style={{ backgroundColor: 'crimson', padding: '50px' }}>
                <h1>invoice</h1>
            </header>
            <button className='btn btn--info' onClick={getInvoice}>
                Get Invoice
            </button>
        </article>
    );
};

InvoiceDoc.propTypes = {};
const mapStateToProps = (state) => ({
    profile: state.profile,
});

export default connect(mapStateToProps)(InvoiceDoc);
