import React, { useState, useEffect } from 'react';
import './InvoiceDoc.scss';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getInputValueByLabelIfVisible } from '../../components/form/utils/customFormQueries';
import settingsIcon from '../../imgs/icons/cogs.png';
import updateIcon from '../../imgs/icons/updateIcon.png';

const InvoiceDoc = ({ profile }) => {
    const profileDetails = profile.details.map((input) => {
        if (input.label === 'Name' && input.addToInvoice) {
            return (
                <li key={input._id}>
                    <b>{input.value}</b>
                </li>
            );
        } else if (input.addToInvoice) {
            return <li key={input._id}>{input.value}</li>;
        }
    });

    return (
        <article id='invoice'>
            <header className='bg'>
                <div>
                    <h1>invoice</h1>
                </div>
                <div className='txt--right'>
                    <button className='icon_iSettings' title='Invoice settings'>
                        <img src={settingsIcon} alt='Invoice settings' />
                    </button>
                    <ul>{profileDetails}</ul>
                </div>
                <button className='icon_iSettings' title='Invoice settings'>
                    <img src={settingsIcon} alt='Invoice settings' />
                </button>
            </header>
            {}
        </article>
    );
};

InvoiceDoc.propTypes = {};
const mapStateToProps = (state) => ({
    profile: state.profile,
});

export default connect(mapStateToProps)(InvoiceDoc);
