import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getInputValueByLabel } from '../../components/form/utils/customFormQueries';

const InvoiceDoc = ({ profile }) => {
    console.log(window.location.origin + '/invoice.css');
    const getInvoice = async () => {
        const invoice = document.getElementById('invoice').innerHTML;
        const invoice_style = `<style>
        header{background:green;}
        @media print {
            header {
                background-color: crimson;
                padding: 50px;
            }
        }
        </style>`;
        const pdf = window.open();
        pdf.document.write('<html><head>');
        // pdf.document.write(invoice_style);
        pdf.document.write(
            `<link rel="stylesheet" type="text/css" href="${window.location.origin}/invoice.css"/>`
        );
        pdf.document.write(
            ` <link
            href="https://fonts.googleapis.com/css?family=Audiowide|Inter:400,600&display=swap"
            rel="stylesheet"
        />`
        );
        pdf.document.write('</head><body>');
        pdf.document.write(invoice);
        pdf.document.write('</body></link>');
        pdf.document.close();
        setTimeout(function () {
            pdf.print();
        }, 500);
    };
    return (
        <article id='invoice'>
            <header className='bg'>
                <h1>invoice</h1>
            </header>
            {getInputValueByLabel(profile.details, 'Name')}
            <button className='btn btn--info' onClick={getInvoice}>
                Save and Download*
            </button>
            <p>
                *{' '}
                <small>
                    You can either print or save the invoice in pdf format. We
                    recomend using Chrome broweser for the best experience. On
                    clicking this button follow your browsers specification on
                    how to handle the process.
                </small>
            </p>
        </article>
    );
};

InvoiceDoc.propTypes = {};
const mapStateToProps = (state) => ({
    profile: state.profile,
});

export default connect(mapStateToProps)(InvoiceDoc);
