import axios from 'axios';
import React, { useState, useEffect, Fragment } from 'react';
import PropTypes from 'prop-types';
import './Register.scss';
import { Redirect, Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { setAlert } from '../../redux/actions/messages';
import Logo from '../../components/logo/Logo';
import FormInput from '../../components/form/components/FormInput';
import FormErrorsDisplay from '../../components/form/components/FormErrorsDisplay';
import { formErrorsStyling } from '../../components/form/utils/formFuns';

const Register = ({ setAlert, endSession }) => {
    const handleCancelataion = () => {
        const inputs = document.querySelectorAll('.register-form input');
        Array.from(inputs).forEach((input) => {
            input.classList.remove('form__input--err');
            input.removeAttribute('aria-label');
        });
        setFormData(initState);
    };
    const deleteAccount = async (e) => {
        e.preventDefault();
        const { password } = formData;
        try {
            const config = {
                headers: {
                    'Content-Type': 'application/json',
                },
            };

            const body = JSON.stringify({ password });
            await axios.post('/api/user/unregister', body, config);
            endSession();
            setAlert(
                'Your account has been deleted successfully. We are sorry to see you going...',
                'success',
                'sign up page',
                false
            );
            setFormData(initState);
        } catch (err) {
            setFormData({
                ...formData,
                errors: err.response.data.errors,
            });
        }
    };
    const initState = {
        email: '',
        password: '',
        password2: '',
        errors: [],
    };
    const [formData, setFormData] = useState(initState);
    useEffect(() => {
        formErrorsStyling(formData.errors);
    }, [formData.errors]);
    return (
        <main className='register' id='main'>
            <section className='register'>
                <Logo />
                <h1 className='sr-only'>Register Your Account</h1>
                <p className='p--lg'>
                    {' '}
                    Creating your account with us is free and super swift.
                </p>
                <form className='register-form' onSubmit={deleteAccount}>
                    <FormInput
                        form={{ formData, setFormData }}
                        type='email'
                        name='email'
                    >
                        Email
                    </FormInput>
                    <FormInput
                        form={{ formData, setFormData }}
                        type='password'
                        name='password'
                    >
                        Password
                    </FormInput>
                    <FormInput
                        form={{ formData, setFormData }}
                        type='password'
                        name='password2'
                    >
                        Confirm the password
                    </FormInput>
                    {formData.errors.length > 0 && (
                        <FormErrorsDisplay
                            errors={formData.errors}
                            label='Register form'
                        />
                    )}
                    <button
                        className='btn'
                        onMouseDown={(e) => e.preventDefault()}
                    >
                        Create account
                    </button>
                </form>
                <p>
                    Already have an account? <Link to='/'>Sign in.</Link>
                </p>
            </section>
        </main>
    );
};

Register.propTypes = {
    setAlert: PropTypes.func,
};
const mapStateToProps = (state) => ({});
const mapDispatchToProps = {
    setAlert,
};
export default connect(mapStateToProps, mapDispatchToProps)(Register);
