import React, { useState, useEffect } from 'react';
import './Calendar.scss';
import { DAYS, DAYS_LEAP, DAYS_OF_THE_WEEK, MONTHS, THIS_YEAR } from './dates';

export function Calendar() {
    const today = new Date();
    const [date, setDate] = useState(today);
    console.log(date.getMonth());
    const day = date.getDate();
    const month = date.getMonth();
    const year = date.getFullYear();
    const first_day = getStartDayOfMonth(date);
    console.log(first_day);

    function getStartDayOfMonth(date) {
        return new Date(year, month, 1).getDay();
        // return new Date(date.getFullYear(), date.getMonth(), 1).getDay();
    }

    function isLeapYear(year) {
        return !(year % 4 || (!(year % 100) && year % 400));
    }

    const days = isLeapYear(date.getFullYear()) ? DAYS_LEAP : DAYS;

    return (
        <article className='calendar'>
            <div className='calendar__header'>
                <button
                    onClick={() =>
                        setDate(
                            new Date(date.getFullYear(), date.getMonth() - 1, 1)
                        )
                    }
                    onMouseDown={(e) => e.preventDefault()}
                >
                    <span>&#x27A4;</span>
                </button>
                <div>
                    <b>
                        {MONTHS[date.getMonth()]} {date.getFullYear()}
                    </b>
                </div>
                <button
                    onClick={() =>
                        setDate(
                            new Date(date.getFullYear(), date.getMonth() + 1, 1)
                        )
                    }
                    onMouseDown={(e) => e.preventDefault()}
                >
                    <span>&#x27A4;</span>
                </button>
            </div>
            <ul>
                {DAYS_OF_THE_WEEK.map((d) => (
                    <li key={'cal-day-names-' + d} className='day day__name'>
                        {d}
                    </li>
                ))}
            </ul>

            <ul>
                {Array(
                    days[date.getMonth()] +
                        (getStartDayOfMonth(date) === 0
                            ? 6
                            : getStartDayOfMonth(date) - 1)
                )
                    .fill(null)
                    .map((_, index, arr) => {
                        const d =
                            index -
                            (getStartDayOfMonth(date) === 0
                                ? 5
                                : getStartDayOfMonth(date) - 2);

                        if (d < 1)
                            return <li key={'calendar-day-' + index}></li>;
                        return (
                            <li className='day' key={'calendar-day-' + index}>
                                <button onMouseDown={(e) => e.preventDefault()}>
                                    {d}
                                </button>
                            </li>
                        );
                    })}
            </ul>
        </article>
    );
}
