import React, { Fragment, useEffect } from 'react';
import './App.scss';
import {
    BrowserRouter as Router,
    Route,
    Switch,
    Redirect,
} from 'react-router-dom';
import { Provider } from 'react-redux';
import store from './redux/store';
import { getCurrentUser, logoutCurrentUser } from './redux/actions/session';

import Login from './pages/login/Login';
import Register from './pages/register/Register';
import Dashboard from './pages/dashboard/Dashboard';

function App() {
    const clearCookiesOnTabClose = (e) => {
        e.preventDefault();
        logoutCurrentUser();
    };
    useEffect(() => {
        store.dispatch(getCurrentUser());
        window.addEventListener('beforeunload', clearCookiesOnTabClose);
        return () => {
            window.removeEventListener('beforeunload', clearCookiesOnTabClose);
        };
    });
    return (
        <Provider store={store}>
            <Router>
                <Fragment>
                    <Switch>
                        <Route exact path='/' component={Login} />
                        <Route exact path='/register' component={Register} />
                        <Route exact path='/dashboard' component={Dashboard} />
                    </Switch>
                </Fragment>
            </Router>
        </Provider>
    );
}

export default App;
