import React, { useState } from 'react';
import { v4 as uuidv4 } from 'uuid';
import FormInput from './FormInput';
import FormErrorsDisplay from './FormErrorsDisplay';

export default function AddCustomFields({ addCustomToFieldsFormState }) {
    const [formData, setFormData] = useState({
        desc: '',
        value: '',
        position: 1,
        show: true,
        custom: true,
        errors: [],
    });
    const handleAdd = () => {
        formData._id = uuidv4();
        delete formData.errors;
        addCustomToFieldsFormState(formData);
        setFormData({
            desc: '',
            value: '',
            position: 1,
            show: true,
            custom: true,
            errors: [],
        });
    };
    return (
        <fieldset className='tile'>
            <legend>
                <b>Add custom fields to your form.</b>
            </legend>
            <FormInput form={{ formData, setFormData }} name='desc' size='auto'>
                Description
            </FormInput>
            <FormInput
                form={{ formData, setFormData }}
                name='value'
                size='auto'
            >
                Value
            </FormInput>
            {formData.errors.length > 0 && (
                <FormErrorsDisplay errors={formData.errors} label='tasks' />
            )}
            <button
                className='btn btn--grey'
                onMouseDown={(e) => e.preventDefault()}
                onClick={handleAdd}
            >
                Add Item
            </button>
        </fieldset>
    );
}
