import {
    GET_ALL_COMPANIES,
    CLEAR_COMPANIES,
    SET_CURRENT_COMPANY,
    UPDATE_COMPANY,
} from '../actions/types';

const initialState = [];

export default function (state = initialState, { type, payload }) {
    switch (type) {
        case GET_ALL_COMPANIES:
            return {
                ...state,
                companies: payload,
            };

        case CLEAR_COMPANIES:
            return [];
        case UPDATE_COMPANY:
            return { ...state, details: [...payload] };
        default:
            return state;
    }
}
