import PropTypes from 'prop-types';
import FormErrorsDisplay from '../../components/forms/FormErrorsDisplay';
import {} from '../../components/forms/formFuns';
import { connect } from 'react-redux';
import axios from 'axios';

const DeleteAccount = ({}) => {
    return (
        <section className='delete-account'>
            <div className='section__heading'>
                <div className='wrapper'>
                    <h2 className='heading heading--sm'>
                        Delete your account.
                    </h2>
                </div>
            </div>
            <div className='section__body'>
                <div className='wrapper'>
                    <p>
                        Once you delete your account, there is no going back.
                        Please be certain.
                    </p>
                    <form onSubmit={onSubmit}>
                        {deleteConfirmation && (
                            <Fragment>
                                <label htmlFor='password'>
                                    Enter password to confirm the deactivation
                                    of your account.
                                </label>
                                <input
                                    type='password'
                                    name='password'
                                    id='password'
                                    onChange={onChange}
                                    className='form__input form__input--sml'
                                />
                            </Fragment>
                        )}
                        {formData.errors.length > 0 && (
                            <FormErrorsDisplay
                                errors={formData.errors}
                                label='delete account'
                            />
                        )}
                        {deleteConfirmation && (
                            <button
                                className='btn btn--grey btn--sibling'
                                onClick={handleCancelation}
                                onMouseDown={(e) => e.preventDefault()}
                            >
                                Cancel
                            </button>
                        )}
                        <button
                            type='submit'
                            className='btn btn--danger'
                            onClick={handleDelete}
                            onMouseDown={(e) => e.preventDefault()}
                        >
                            {!deleteConfirmation && (
                                <span className='sr-only'>
                                    Display form to{' '}
                                </span>
                            )}
                            Delete Account
                            {deleteConfirmation && (
                                <span className='sr-only'> now </span>
                            )}
                        </button>
                    </form>
                </div>
            </div>
        </section>
    );
};

DeleteAccount.propTypes = {};
const mapStateToProps = (state) => ({});
const mapDispatchToProps = {
    setAlert,
    endSession,
};
export default connect(mapStateToProps, mapDispatchToProps)(DeleteAccount);
