import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getProfile, updateProfileDetails } from '../../redux/actions/profile';
import DetailsDisplayTable from '../../components/form/components/DetailsDisplayTable';
import { saveChangesOnLeave } from '../../components/form/utils/handleUnsavedChanges';

export const ProfileDetails = ({
    profile: { details },
    getProfile,
    updateProfileDetails,
}) => {
    const [state, setsState] = useState({ details });
    //save changes on leave
    //deep copy of state on component mount
    //insted of calling DB
    const initialState = JSON.parse(JSON.stringify(details));
    console.log('PD', details);
    useEffect(() => {
        console.log('PD UE', details);
        return async () => {
            console.log('PD UE CB', details);
            console.log('PD UE CB INIT', initialState);
            console.log('PD UE State', state);
            // await saveChangesOnLeave(
            //     initialState,
            //     details,
            //     '/api/profile' //api call,
            // );
            // getProfile();
        };
    }, [state]);
    return (
        <section>
            <h2 className='sr-only'>Profile Details .</h2>
            <DetailsDisplayTable
                details={details}
                updateState={updateProfileDetails}
            />
        </section>
    );
};

ProfileDetails.propTypes = {
    profile: PropTypes.object,
    getProfile: PropTypes.func,
    updateProfileDetails: PropTypes.func,
};

const mapStateToProps = (state) => ({
    profile: state.profile,
});

const mapDispatchToProps = {
    getProfile,
    updateProfileDetails,
};

export default connect(mapStateToProps, mapDispatchToProps)(ProfileDetails);
