import {
    GET_PROFILE,
    REMOVE_PROFILE,
    UPDATE_PROFILE,
    UPDATE_PROFILE_WITH_ORDER_CHANGE,
} from '../actions/types';

const initialState = {};

export default function (state = initialState, { type, payload }) {
    switch (type) {
        case GET_PROFILE:
            return { ...payload };
        case UPDATE_PROFILE:
            return { ...state, details: [...payload] };
        case UPDATE_PROFILE_WITH_ORDER_CHANGE:
            state.details.splice(
                payload.arr[1],
                0,
                state.details.splice(payload.arr[0], 1)[0]
            );
            return { ...state, details };
        case REMOVE_PROFILE:
            return {};
        default:
            return state;
    }
}
