import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { Document, Page, Text, View, Image, Font } from '@react-pdf/renderer';
import { styles } from './invoiceDocStyles';
import { getInputValueByLabel } from '../../components/form/utils/customFormQueries';

const InvoiceDoc = ({ profile }) => {
    return <Document>{profile && <DocPage profile={profile} />}</Document>;
};

InvoiceDoc.propTypes = {};
const mapStateToProps = (state) => ({
    profile: state.profile,
});
// export default InvoiceDoc;
export default connect(mapStateToProps)(InvoiceDoc);

const Subtitle = ({ children, ...props }) => (
    <Text style={styles.subtitle} {...props}>
        {children}
    </Text>
);

const DocPage = ({ profile }) => {
    const [invoiceState, setInvoiceState] = useState({
        iProfile: {
            name: 'blank',
        },
    });
    useEffect(() => {
        if (profile.details) {
            console.log(getInputValueByLabel(profile.details, 'Name'));
            const name = getInputValueByLabel(profile.details, 'Name');
            setInvoiceState({});
            console.log(invoiceState);
        }
    }, [profile]);
    const { iProfile } = invoiceState;
    return (
        <Page size='A4' style={styles.page}>
            <View style={styles.section}>
                <Text>Section #1</Text>
                {/* <Text>{iProfile.name}</Text> */}
            </View>
            <View style={styles.section}>
                <Text>Section #2</Text>
            </View>
        </Page>
    );
};
