import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import Page from '../../components/page/Page';
import updateIcon from '../../imgs/icons/updateIcon.png';
import listIcon from '../../imgs/icons/list.png';

import './Profile.scss';

const Profile = ({ updated }) => {
    //detaisl | form
    const [currentTab, setCurrentTab] = useState('details');
    return (
        <Page>
            <nav aria-label="Profile's submenu" className='submenu'>
                <ul
                    aria-label="Profile's submenu links"
                    className='submenu__list'
                >
                    <li className='submenu__link'>
                        <button
                            className='submenu__btn'
                            onClick={() => {
                                setCurrentTab('details');
                            }}
                            onMouseDown={(e) => e.preventDefault()}
                        >
                            <img
                                src={listIcon}
                                className='submenu__icon'
                                alt=''
                            />
                            Details
                        </button>
                    </li>
                    <li className='submenu__link'>
                        <button
                            className='submenu__btn'
                            onClick={() => {
                                setCurrentTab('form');
                            }}
                            onMouseDown={(e) => e.preventDefault()}
                        >
                            <img
                                src={updateIcon}
                                className='submenu__icon'
                                alt=''
                            />
                            Update Profile
                        </button>
                    </li>
                </ul>
            </nav>
            {!updated && (
                <div className='tile'>Please create your profile now.</div>
            )}
            {currentTab === 'details' && (
                <div className='tile tile--md'>details</div>
            )}
            {currentTab === 'form' && <div className='tile tile--sm'>form</div>}
        </Page>
    );
};

Profile.propTypes = {
    updated: PropTypes.bool.isRequired,
};
const mapStateToProps = (state) => ({
    updated: state.profile.updated,
});
export default connect(mapStateToProps)(Profile);
