import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

const InvoiceDoc = ({ profile }) => {
    console.log(window.location.origin + '/invoice.css');
    const getInvoice = async () => {
        const invoice = document.getElementById('invoice').innerHTML;
        const invoice_style = `<style>
        header{background:green;}
        @media print {
            header {
                background-color: crimson;
                padding: 50px;
            }
        }
        </style>`;
        // const pdf = window.open();
        document.write('<html><head>');
        // pdf.document.write(invoice_style);
        document.write(
            `<link rel="stylesheet" type="text/css" href="${window.location.origin}/invoice.css"/>`
        );
        document.write(
            ` <link
            href="https://fonts.googleapis.com/css?family=Audiowide|Inter:400,600&display=swap"
            rel="stylesheet"
        />`
        );
        document.write('</head><body>');
        document.write(invoice);
        document.write('</body></link>');
        // pdf.document.close();
        setTimeout(function () {
            print();
        }, 500);
    };
    return (
        <article id='invoice'>
            <header className='bg'>
                <h1>invoice</h1>
            </header>
            <button className='btn btn--info' onClick={getInvoice}>
                Get Invoice
            </button>
        </article>
    );
};

InvoiceDoc.propTypes = {};
const mapStateToProps = (state) => ({
    profile: state.profile,
});

export default connect(mapStateToProps)(InvoiceDoc);
