import React, { Fragment } from 'react';
import PropTypes from 'prop-types';
import { inputOnChange, checkboxOnChange } from './formFuns';

const FormInput = ({
    type = 'text',
    name,
    size = 'sml',
    children,
    form: { formData, setFormData },
}) => {
    const onChange = (e) => {
        inputOnChange(e, formData, setFormData);
    };
    const handleCheckboxOnChange = (e) => {
        checkboxOnChange(e, formData, setFormData);
    };

    switch (type) {
        case 'checkbox':
            return (
                <Fragment>
                    <div className='input-checkbox'>
                        <label htmlFor={name}>{children}</label>
                        {formData.showAcronym && (
                            <input
                                type={type}
                                name={name}
                                id={name}
                                onChange={handleCheckboxOnChange}
                                className={'form__input'}
                                onMouseDown={(e) => e.preventDefault()}
                                checked
                            />
                        )}
                        {!formData.showAcronym && (
                            <input
                                type={type}
                                name={name}
                                id={name}
                                onChange={handleCheckboxOnChange}
                                className={'form__input'}
                                onMouseDown={(e) => e.preventDefault()}
                            />
                        )}
                        <span className='input-checkbox__checkmark'></span>
                    </div>
                </Fragment>
            );
        case 'textarea':
            return (
                <Fragment>
                    <label htmlFor={name}>{children}</label>
                    <textarea
                        name={name}
                        id={name}
                        onChange={onChange}
                        className='txtarea txtarea--md'
                        value={formData[name] || ''}
                    />
                </Fragment>
            );
        case 'text':
        default:
            return (
                <Fragment>
                    <label htmlFor={name}>{children}</label>
                    <input
                        type={type}
                        name={name}
                        id={name}
                        onChange={onChange}
                        className={'form__input form__input--' + size}
                        value={formData[name] || ''}
                    />
                </Fragment>
            );
    }
};

FormInput.propTypes = {
    name: PropTypes.string.isRequired,
    children: PropTypes.string.isRequired,
    form: PropTypes.object.isRequired,
};

export default FormInput;
