import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import InvoiceDoc from './InvoiceDoc';

export const NewInvoice = () => {
    const downloadInvoice = () => {
        const invoice = document.getElementById('invoice').innerHTML;
        const pdf = window.open();
        pdf.document.write('<html><head>');
        pdf.document.write(
            `<link rel="stylesheet" type="text/css" href="${window.location.origin}/invoice.css"/>`
        );
        pdf.document.write(
            ` <link
            href="https://fonts.googleapis.com/css?family=Audiowide|Inter:400,600&display=swap"
            rel="stylesheet"
        />`
        );
        pdf.document.write('</head><body>');
        pdf.document.write(invoice);
        pdf.document.write('</body></link>');
        pdf.document.close();
        setTimeout(function () {
            pdf.print();
        }, 100);
    };
    return (
        <section className='company-invoice'>
            <h2 className='sr-only'>Create a new invoice.</h2>
            <InvoiceDoc />
            <button className='btn btn--info' onClick={saveInvoice}>
                Save
            </button>
            <button className='btn btn--info' onClick={downloadInvoice}>
                Download*
            </button>
            <p>
                *{' '}
                <small>
                    You can either print or save the invoice in pdf format. We
                    recomend using Chrome broweser for the best experience. On
                    clicking this button follow your browsers specification on
                    how to handle the process.
                </small>
            </p>
        </section>
    );
};

NewInvoice.propTypes = {};

const mapStateToProps = (state) => ({});

const mapDispatchToProps = {};

export default connect(mapStateToProps, mapDispatchToProps)(NewInvoice);
