import React, { Fragment } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { connect } from 'react-redux';
import Logo from '../../components/logo/Logo';
import { startSession } from '../../redux/actions/session';

const Login = ({ authenticated, startSession }) => {
    const onSubmit = async (e) => {
        e.preventDefault();
        try {
            const body = JSON.stringify({
                email: 'me@me.com',
                password: 'Qwert1234',
            });
            const config = {
                headers: {
                    'Content-Type': 'application/json',
                },
            };
            const res = await axios.post('/api/user/login', body, config);
            startSession(res.data);
        } catch (err) {
            console.log(err);
        }
    };
    return (
        // <Fragment>
        //     {!authenticated && (
        //         <main className='login' id='main'>
        //             <form onSubmit={onSubmit}>
        //                 <button
        //                     type='submit'
        //                     className='btn btn--form btn--theme'
        //                     onMouseDown={(e) => e.preventDefault()}
        //                 >
        //                     Sign In
        //                 </button>
        //             </form>
        //             <p className='p--sml'>
        //                 Need an account?{' '}
        //                 <Link to='/register'>Sign up now.</Link>
        //             </p>
        //         </main>
        //     )}
        // </Fragment>
        <main className='login' id='main'>
            <section className='login__bg'>
                <div className='cover cover--theme'>
                    <Logo />
                </div>
            </section>
            <section className='login__form'>
                <h1 className='heading heading--md'>Login</h1>
            </section>
        </main>
    );
};
const mapStateToProps = (state) => ({
    authenticated: state.session.authenticated,
});
const mapDispatchToProps = { startSession };
export default connect(mapStateToProps, mapDispatchToProps)(Login);
