import React, { useState } from 'react';
import { v4 as uuidv4 } from 'uuid';
import FormInput from './FormInput';
import FormErrorsDisplay from './FormErrorsDisplay';

export default function AddCustomFields({ addCustomToFieldsFormState }) {
    const [formData, setFormData] = useState({
        customFieldDesc: '',
        customFieldValue: '',
        customFieldPosition: null,
        errors: [],
    });
    const handleAdd = () => {
        if (!customFieldDesc) {
        }
        const {
            customFieldDesc,
            customFieldValue,
            customFieldPosition,
        } = formData;
        //create customised field
        const customField = {
            _id: uuidv4(),
            desc: customFieldDesc,
            value: customFieldValue,
            position: customFieldPosition,
            show: true,
            custom: true,
        };
        addCustomToFieldsFormState(customField);
        //reset state
        setFormData({
            customFieldDesc: '',
            customFieldValue: '',
            customFieldPosition: null,
            errors: [],
        });
    };
    return (
        <fieldset className='tile'>
            <legend>
                <b>Add custom fields to your form.</b>
            </legend>
            <FormInput form={{ formData, setFormData }} name='desc' size='auto'>
                Description
            </FormInput>
            <FormInput
                form={{ formData, setFormData }}
                name='value'
                size='auto'
            >
                Value
            </FormInput>
            {formData.errors.length > 0 && (
                <FormErrorsDisplay errors={formData.errors} label='' />
            )}
            <button
                className='btn btn--grey'
                onMouseDown={(e) => e.preventDefault()}
                onClick={handleAdd}
            >
                Add Item
            </button>
        </fieldset>
    );
}
