import { START_SESS, END_SESS } from '../actions/types';

const initialState = {
    user: null,
    authenticated: false,
    currentCompany: null,
    currentInvoice: null,
};

export default function (state = initialState, { type, user }) {
    switch (type) {
        case START_SESS:
            return {
                ...state,
                user,
                authenticated: true,
            };
        case END_SESS:
            return {
                ...state,
                user: null,
                authenticated: false,
                currentCompany: null,
                currentInvoice: null,
            };
        default:
            return state;
    }
}
