import React, { useState, useEffect, Fragment } from 'react';
import './InvoiceDoc.scss';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import {} from '../../../../redux/actions/invoice';
import plusIcon from '../../../../imgs/icons/plusIcon.png';
import updateIcon from '../../../../imgs/icons/updateIcon.png';
import tasksIcon from '../../../../imgs/icons/tasksIcon.png';

const InvoiceDocItems = ({ invoice }) => {
    //display recepient details in the invoice
    const tasksDetails = invoice
        .tasksfilter((t) => t.addToInvoice)
        .map((t) => {});

    const [showTasks, setShowTasks] = useState(false);
    const open_tasks = async () => {
        if (showTasks === true) return setShowTasks(false);
        await setShowTasks(true);
        //set focus on first button for screen reader users
        // document.querySelector('.invoice__company-details button').focus();
    };

    const [showTaskForm, setShowTaskForm] = useState(false);
    const open_task_form = async (date) => {
        if (showTaskForm === true) return setShowTaskForm(false);
        await setShowTaskForm(true);
        //set focus on first button for screen reader users
        // document
        //     .querySelector('.calendar__issue-date .days__list button')
        //     .focus();
    };

    return (
        <Fragment>
            <section className='invoice-items'>
                <h2 className='sr-only'>Invoice items display.</h2>
                <header>
                    <button
                        className='invoice__btn'
                        title='Display All Items'
                        onMouseDown={(e) => e.preventDefault()}
                        onClick={open_tasks}
                    >
                        <img src={tasksIcon} alt='Display All Items' />
                    </button>
                </header>

                {showDueDateCalendar && (
                    <div className='calendar__due-date'>
                        <h3>
                            Invoice <strong>due date</strong>.
                        </h3>
                        <Calendar cb={(date) => updateInvoiceDueDate(date)} />
                        <button
                            className='close'
                            onClick={() => setShowDueDateCalendar(false)}
                            onMouseDown={(e) => e.preventDefault()}
                        >
                            <span>&times;</span>
                        </button>
                    </div>
                )}
            </section>
        </Fragment>
    );
};

InvoiceDocItems.propTypes = {
    invoice: PropTypes.object,
};
const mapStateToProps = (state) => ({
    invoice: state.invoice,
});
const mapDispatchToProps = {};

export default connect(mapStateToProps, mapDispatchToProps)(InvoiceDocItems);
