import React, { useState, useEffect, Fragment } from 'react';
import './Form.scss';
import FormErrorsDisplay from './FormErrorsDisplay';
import { isRequired, isValidated } from './validations';
import { formErrorsStyling } from './formFuns';
import AddCustomFields from './AddCustomFields';
import RemoveCustomFields from './RemoveCustomFields';

export default function CustomBuiltForm({ fields, url }) {
    const [formState, setFormState] = useState(fields);
    const [errors, setErrors] = useState([]);
    const addCustomToFieldsFormState = (field) => {
        const position = field.position;
        delete field.position;
        formState.splice(position, 0, field);
        setFormState([...formState]);
        console.log(formState[0]);
    };
    const removeCustomFieldsFromFormState = (id) => {
        const newFields = formState.filter((field) => field._id !== id);
        setFormState(newFields);
    };
    const updateInput = (e, id) => {
        const inputs = document.querySelectorAll('.form__submit input');
        const index = Array.from(inputs).findIndex(
            (el) => el.getAttribute('id') === id
        );
        formState[index].value = e.target.value;
        setFormState([...formState]);
        //remove errors styling related to the input
        //empty errors array
        e.target.classList.remove('form__input--err');
        e.target.removeAttribute('aria-label');
        errors.filter((err) => err.param !== name);
    };

    const onSubmit = (e) => {
        e.preventDefault();
        const errArr = [];
        isRequired(formState, errArr);
        isValidated(formState, errArr);
        if (errArr.length > 0) {
            setErrors(errArr);
            return;
        }
    };
    useEffect(() => {
        formErrorsStyling(errors);
    }, [errors]);
    const customFields = formState.filter((field) => field.custom);
    return (
        <form onSubmit={onSubmit} className='form form__submit'>
            <div>
                <fieldset>
                    <legend>
                        <span className='sr-only'>
                            Use this form to create or update the details of
                            this page.
                        </span>
                        * Regired fields.
                    </legend>
                    {formState.map((input, i) => {
                        const {
                            _id,
                            label,
                            inputType,
                            value,
                            required,
                        } = input;
                        return (
                            <Fragment key={_id}>
                                <label htmlFor={_id}>
                                    {i + 1}. {label}
                                    {required ? '*' : null}
                                </label>
                                <input
                                    id={_id}
                                    type={inputType}
                                    value={value}
                                    onChange={(e) => updateInput(e, _id)}
                                    className={'form__input'}
                                />
                            </Fragment>
                        );
                    })}
                    <button
                        className='btn btn--info'
                        onMouseDown={(e) => e.preventDefault()}
                    >
                        Submit
                    </button>
                </fieldset>
                {errors.length > 0 && (
                    <FormErrorsDisplay errors={errors} label='Page main form' />
                )}
            </div>
            <div>
                <AddCustomFields
                    addCustomToFieldsFormState={addCustomToFieldsFormState}
                    maxPosition={formState.length}
                />
                {customFields.length > 0 && (
                    <RemoveCustomFields
                        customFields={customFields}
                        removeCustomFieldsFromFormState={
                            removeCustomFieldsFromFormState
                        }
                    />
                )}
            </div>
        </form>
    );
}
