import { LOGIN_USER, LOGOUT_CURRENT_USER } from '../actions/types';

const initialState = {
    user: null,
    authenticated: false,
};

export default function (state = initialState, { type, user }) {
    switch (type) {
        case LOGIN_USER:
            return {
                ...state,
                user,
                authenticated: true,
            };
        case LOGOUT_CURRENT_USER:
            return {
                ...state,
                user: null,
                authenticated: false,
            };
        default:
            return state;
    }
}
