export const getInputObjById = (arr, id) => {
    return arr.find((input) => input._id === id);
};

export const getInputValueByLabel = (arr, l) => {
    return arr.find((input) => input.label === l).value;
};

//arr1 -> obj with arr2 ( obj with value property  that position in arr2 chnages dinamicaly )
export const sortInputsByNamesAlphabeticaly = (arr) => {
    const arrAllNames = [];
    arr.forEach((commpany) => {
        arrAllNames.push(getInputValueByLabel(company.details, 'Name'));
    });
    arr.sort((a, b) => {
        console.log(getInputValueByLabel(a.details, 'Name'));
        console.log(getInputValueByLabel(b.details, 'Name'));
        getInputValueByLabel(a.details, 'Name').localeCompare(
            getInputValueByLabel(b.details, 'Name')
        );
    });
    return arr;
};
