import React from 'react';
import arrowIcon from '../../../imgs/icons/arrow.png';

export default function DetailsDisplayTable({ details }) {
    return (
        <table className='details-table'>
            <caption className='sr-only'></caption>
            <thead>
                <tr className='sr-only'>
                    <th scope='col'>Add task to a new invoice</th>
                    <th scope='col'>Description</th>
                    <th scope='col'>Value</th>
                    <th scope='col'>Move Item Up</th>
                    <th scope='col'>Move Item Down</th>
                </tr>
            </thead>
            <tbody>
                {details
                    .filter((item) => item.value)
                    .map((item) => {
                        return (
                            <tr key={item._id}>
                                <span>
                                    <th scope='row'>{item.label}:</th>
                                    <td className='value'>{item.value}</td>
                                </span>
                                <td
                                // onClick={() => {
                                //     task.addToNextInvoice = !addToNextInvoice;
                                //     toggleTaskInvoiceDisplay(_id, task);
                                // }}
                                >
                                    <button
                                        onMouseDown={(e) => e.preventDefault()}
                                        className='btn--sqr'
                                    >
                                        {item.show ? (
                                            <b>&#45;</b>
                                        ) : (
                                            <b>&#43;</b>
                                        )}
                                    </button>
                                </td>
                                <td>
                                    <button
                                        onMouseDown={(e) => e.preventDefault()}
                                        className='arrow-up btn--sqr'
                                    >
                                        <img
                                            src={arrowIcon}
                                            alt='move item up'
                                        />
                                    </button>
                                </td>
                                <td>
                                    <button
                                        onMouseDown={(e) => e.preventDefault()}
                                        className='arrow-down btn--sqr'
                                    >
                                        <img
                                            src={arrowIcon}
                                            alt='move item up'
                                        />
                                    </button>
                                </td>
                            </tr>
                        );
                    })}
            </tbody>
        </table>
    );
}
