import {
    START_SESS,
    END_SESS,
    SET_PROFILE_TAB,
    SET_COMPANY_TAB,
    SESS_UPDATED,
} from '../actions/types';

const initialState = {
    authenticated: false,
    currentProfileTab: 'details', //detaisl | form
    currentCompanyTab: 'tasks', //details | tasks |invoices | update | delete,
    updates: false,
};

export default function (state = initialState, { type, payload }) {
    switch (type) {
        case START_SESS:
            return {
                ...state,
                authenticated: true,
            };
        case END_SESS:
            return {
                ...state,
                authenticated: false,
                updates: false,
            };
        case SET_PROFILE_TAB:
            return {
                ...state,
                currentProfileTab: payload,
            };
        case SET_COMPANY_TAB:
            return {
                ...state,
                currentCompanyTab: payload,
            };
        case SESS_UPDATED:
            return {
                ...state,
                updates: payload,
            };
        default:
            return state;
    }
}
