import axios from 'axios';
import { GET_PROFILE, END_SESS } from './types';
import { setAlert } from './messages';

export const getProfile = () => async (dispatch) => {
    try {
        const res = await axios.get('/api/proffile');
        dispatch({
            type: GET_PROFILE,
            profile: res.data,
        });
    } catch (err) {
        console.error('ERROR ON PROFILE LOADING', err);
        console.log(err);
        dispatch(
            setAlert(
                "We are sorry, but there has been an error while we tried to load your profile's details.",
                'danger',
                null,
                false,
                15000
            )
        );
    }
};
