import { START_SESS, END_SESS } from '../actions/types';

const initialState = {
    user: null,
    authenticated: false,
};

export default function (state = initialState, { type, user }) {
    switch (type) {
        case START_SESS:
            return {
                ...state,
                user,
                authenticated: true,
            };
        case END_SESS:
            localStorage.clear();
            return {
                ...state,
                user: null,
                authenticated: false,
            };
        default:
            return state;
    }
}
