import React, { useState, useEffect, Fragment } from 'react';
import './InvoiceDoc.scss';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { changeInvoiceColor } from '../../../../redux/actions/invoice';
import settingsIcon from '../../../../imgs/icons/cogs.png';
import updateIcon from '../../../../imgs/icons/updateIcon.png';

const InvoiceDocHeader = ({ profile, changeInvoiceColor }) => {
    const [settings, setSettings] = useState(false);
    const profileDetails = profile.details.map((input) => {
        if (input.label === 'Name' && input.addToInvoice) {
            return (
                <li key={input._id}>
                    <b>{input.value}</b>
                </li>
            );
        } else if (input.addToInvoice) {
            return <li key={input._id}>{input.value}</li>;
        }
    });

    const COLORS = [
        ['#000', 'black'],
        ['#5F7C8A', 'grey'],
        ['#785447', 'brown'],
        ['#FF5623', 'bright-orange'],
        ['#EB9346', 'orange'],
        ['#FFC108', 'yellow'],
        ['#CDDD39', 'yellow-green'],
        ['#8BC34A', 'light-green'],
        ['#4BAF50', 'green'],
        ['#009688', 'dark-green'],
        ['#01BCD5', 'see-blue'],
        ['#03A9F4', 'light-blue'],
        ['#2065A8', 'blue'],
        ['#203354', 'dark-blue'],
        ['#3F50B6', 'dark-purple'],
        ['#673BB7', 'purple'],
        ['#9C27B0', 'purple-pink'],
        ['#E91F63', 'pink'],
        ['#D5133A', 'dark-pink'],
        ['#F44337', 'red'],
    ];
    const colorPicker = COLORS.map((color) => (
        <li
            key={'color-picker-' + color[0]}
            style={{ backgroundColor: `${color[0]}` }}
            title={color[0]}
        >
            <button
                onClick={() => changeInvoiceColor(color[1])}
                onMouseDown={(e) => e.preventDefault()}
            >
                <span className='sr-only'>color[1]</span>
            </button>
        </li>
    ));
    return (
        <Fragment>
            {settings && (
                <section className='color-picker'>
                    <h3 id='color-picker'>Choose invoice color theme.</h3>
                    <ul aria-labelledby='color-picker'>{colorPicker}</ul>
                    <button
                        className='close'
                        onClick={() => setSettings(false)}
                        onMouseDown={(e) => e.preventDefault()}
                    >
                        &times;
                    </button>
                </section>
            )}
            <header className={`bg-${invoice.color}`}>
                <button
                    className='invoice__btn icon_iSettings'
                    title='Change invoice settings'
                    onClick={() => setSettings(true)}
                    onMouseDown={(e) => e.preventDefault()}
                >
                    <img src={settingsIcon} alt='Invoice settings' />
                </button>
                <div>
                    <h1>invoice</h1>
                </div>
                <div className='txt--right'>
                    <button
                        className='invoice__btn'
                        title='Change Profile details'
                    >
                        <img src={updateIcon} alt='Change Profile details' />
                    </button>
                    <ul>{profileDetails}</ul>
                </div>
            </header>
        </Fragment>
    );
};

InvoiceDocHeader.propTypes = {
    profile: PropTypes.object,
    changeInvoiceColor: PropTypes.func,
};
const mapStateToProps = (state) => ({
    profile: state.profile,
});
const mapDispatchToProps = {
    changeInvoiceColor,
};

export default connect(mapStateToProps, mapDispatchToProps)(InvoiceDocHeader);
