import React, { useState, useEffect, Fragment } from 'react';
import axios from 'axios';
import { v4 as uuidv4 } from 'uuid';
import FormInput from '../components/FormInput';
import FormErrorsDisplay from '../components/FormErrorsDisplay';
import { formErrorsStyling } from '../utils/formFuns';

export default function TaskForm({ company, update }) {
    const [formData, setFormData] = useState({
        description: '',
        qty: '',
        rate: '',
        amount: '',
        errors: [],
    });
    const handleAdd = async (e) => {
        e.preventDefault();
        const { description, qty, rate, amount } = formData;
        const errors = [];
        if (!description.trim()) {
            const error = {
                param: 'description',
                msg: 'Please describe the task.',
            };
            errors.push(error);
        }
        if (errors.length > 0) {
            setFormData({
                ...formData,
                errors,
            });
            return;
        }

        try {
            const config = {
                headers: {
                    'Content-Type': 'application/json',
                },
            };
            //create task
            const task = {
                _id: uuidv4(),
                description,
                qty,
                rate,
                amount,
                addToInvoice: true,
                createdAt: new Date(),
            };
            let body = { task };
            if (update) body.update = update;
            body = JSON.stringify(body);
            const res = await axios.post('/api/companies/tasks/', body, config);
            //get all tasks
            //reset state
            setFormData({
                description: '',
                qty: '',
                rate: '',
                amount: '',
                errors: [],
            });
        } catch (err) {
            console.log(err);
        }
    };

    useEffect(() => {
        formErrorsStyling(formData.errors);
    }, [formData.errors]);
    return (
        <Fragment>
            <fieldset className='add-custom-fields'>
                <legend>
                    <b>Add a custom field.</b>
                </legend>
                <FormInput
                    form={{ formData, setFormData }}
                    name='customFieldLabel'
                    size='auto'
                >
                    Label / Description*
                </FormInput>
                <FormInput
                    form={{ formData, setFormData }}
                    name='customFieldValue'
                    size='auto'
                >
                    Value*
                </FormInput>
                <p>
                    You can determin the display position of the new field in
                    the details list.
                </p>
                {/* <label htmlFor='customFieldPosition'>Choose a position:</label>
                <select id='customFieldPosition' onChange={getSelectedPosition}>
                    {[...Array(++maxPosition).keys()].map((key) => (
                        <option
                            value={key}
                            key={'position-of-custom-field-' + key}
                        >
                            {key + 1}
                        </option>
                    ))}
                </select> */}
                {/* <br /> */}
                <button
                    className='btn btn--grey'
                    onMouseDown={(e) => e.preventDefault()}
                    onClick={handleAdd}
                >
                    Add Item
                </button>
            </fieldset>
            {formData.errors.length > 0 && (
                <FormErrorsDisplay
                    errors={formData.errors}
                    label='Add custom field form'
                />
            )}
        </Fragment>
    );
}
