import React, { useState, useEffect } from 'react';
import './Form.scss';
import AddCustomFields from './AddCustomFields';
import RemoveCustomFields from './RemoveCustomFields';

export default function Form({ fields, url }) {
    const [formState, setFormState] = useState(fields);
    const addCustomToFieldsFormState = (field) => {
        setFormState([...formState, field]);
    };
    const removeCustomFieldsFromFormState = () => {};
    const onSubmit = (e) => {
        e.preventDefault();
    };
    const customFields = formState.filter((field) => field.custom);
    return (
        <form onSubmit={onSubmit} className='form form__submit'>
            <fieldset className='tile'>
                <legend>
                    <span class='sr-only'>
                        Use this form to create or update the details of this
                        page.
                    </span>
                    * Regired fields.
                </legend>
                {formState.map((field) => (
                    <div>{field.desc}</div>
                ))}
                <button className='btn btn--info'>Submit</button>
            </fieldset>
            <div>
                <AddCustomFields
                    addCustomToFieldsFormState={addCustomToFieldsFormState}
                />
                <RemoveCustomFields
                    removeCustomFieldsFromFormState={
                        removeCustomFieldsFromFormState
                    }
                />
            </div>
        </form>
    );
}
