import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import axios from 'axios';
import { setAlert } from '../../redux/actions/messages';
import { logoutCurrentUser } from '../../redux/actions/session';

export const Header = ({ setAlert, logoutCurrentUser }) => {
    let date = new Date();
    const options = {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
    };
    date = date.toLocaleDateString(undefined, options);
    const profile = {};
    const messages = [
        `Today is ${date}.`,
        `${
            profile.created
                ? `Welcome back! Your last login was on ...`
                : 'Welcome to <b class="font-logo">invoice</b> . Please create your profile.'
        }`,

        'Happy <b class="font-logo">invoicing</b> !',
    ];
    let interval;
    const tile = document.querySelector('.header__messages');
    const runInterval = () => {
        let i = 0;
        tile.innerHTML = `<span>${messages[1]}</span>`;
        interval = setInterval(() => {
            tile.innerHTML = '';
            tile.innerHTML = `<span>${messages[i]}</span>`;
            i++;
            if (i === messages.length) i = 0;
        }, 2000);
    };

    useEffect(() => {
        const tile = document.querySelector('.header__messages');
        runInterval();
        tile.addEventListener('mouseover', () => {
            clearInterval(interval);
        });
        tile.addEventListener('mouseout', runInterval);
        return () => {
            clearInterval(interval);
        };
    }, [tile]);
    const logout = async () => {
        try {
            await axios.delete('/api/user');
            setAlert(
                'You have been logged out successfully.',
                'success',
                'login page',
                false
            );
            logoutCurrentUser();
        } catch (err) {
            console.log('logout err', err);
        }
    };
    return (
        <header className='header'>
            <Link
                to='/dashboard/profile'
                onMouseDown={(e) => e.preventDefault()}
                className='header__logo tile'
                onClick={() => {
                    setAlert(
                        `Profile's settings have been now desplayed on the page `,
                        'success'
                    );
                }}
            >
                invoice
            </Link>
            <div className='header__messages tile'></div>
            <button
                className='header__logout tile'
                onMouseDown={(e) => e.preventDefault()}
                onClick={logout}
            >
                Sign Out
            </button>
        </header>
    );
};

Header.propTypes = {
    setAlert: PropTypes.func.isRequired,
    logoutCurrentUser: PropTypes.func.isRequired,
};

const mapDispatchToProps = {
    setAlert,
    logoutCurrentUser,
};

export default connect(null, mapDispatchToProps)(Header);
