import { SET_INVOICE_INIT_STATE, CHANGE_INVOICE_COLOR } from '../actions/types';

const initialState = {
    // _id, createdAt, savedAs,
    color: 'orange',
    notes: null,
    profile: [],
    company: [],
    tasks: [],
};

export default function (state = initialState, { type, payload }) {
    switch (type) {
        case SET_INVOICE_INIT_STATE:
            return { ...payload };
        case CHANGE_INVOICE_COLOR:
            return { ...state, color: payload };
        default:
            return state;
    }
}
