import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getCurrentUser } from '../../redux/actions/user';
import Header from './Header';
import Nav from './Nav';
import './Page.scss';

const Page = ({ user, getCurrentUser, children }) => {
    useEffect(() => {
        getCurrentUser();
