import { GET_CURRENT_USER, LOGOUT_CURRENT_USER } from '../actions/types';

const initialState = null;

export default function (state = initialState, { type, payload }) {
    switch (type) {
        case GET_CURRENT_USER:
            return {
                ...state,
                isRegistered: true,
                loading: false,
            };
        default:
            return state;
    }
}
