import {
    GET_CURRENT_USER,
    LOGIN_USER,
    LOGOUT_CURRENT_USER,
} from '../actions/types';

const initialState = null;

export default function (state = initialState, { type, payload }) {
    switch (type) {
        case LOGIN_USER:
            return {
                ...state,
                user: payload,
                authenticated: true,
            };
        default:
            return state;
    }
}
