import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import Page from '../../components/page/Page';
import * as jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

export const Invoice = () => {
    const downlaod = () => {
        const printArea = document.getElementById('invoiceToPDF');

        html2canvas(printArea).then((canvas) => {
            var imgData = canvas.toDataURL('image/png');
            console.log('Report Image URL: ' + imgData);
            var doc = new jsPDF('p', 'mm', [297, 210]); //210mm wide and 297mm high

            doc.addImage(imgData, 'PNG', 0, 0);
            doc.save('sample.pdf');
            // const dataURL = canvas.toDataURL('image/png');
            // const pdf = new jsPDF('p', 'mm', [297, 210]);

            // pdf.addImage(dataURL, 'PNG', 20, 20, 180, 160);

            // pdf.save('saved.pdf');
        });
        // html2canvas(document.body).then(function (canvas) {
        //     var img = canvas.toDataURL('image/png');
        //     var doc = new jsPDF();
        //     doc.addImage(img, 'JPEG', 10, 10);
        //     // doc.save('test.pdf');
        //     doc.output('dataurlnewwindow');
        // });
        // var doc = new jsPDF();
        // var elementHandler = {
        //     '#ignorePDF': function (element, renderer) {
        //         return true;
        //     },
        // };
        // var source = document.getElementById('invoice');
        // doc.addHTML(source, function () {
        //     // pdf.save('Test.pdf');
        //     doc.output('dataurlnewwindow');
        // });
    };

    return (
        <Page>
            <article id='invoiceToPDF'>
                <header style={{ backgroundColor: 'crimson', padding: '40px' }}>
                    invoice
                </header>
            </article>
            <button className='btn btn--info' onClick={downlaod}>
                Download pdf
            </button>
        </Page>
    );
};

Invoice.propTypes = {};

const mapStateToProps = (state) => ({});

const mapDispatchToProps = {};

export default connect(mapStateToProps, mapDispatchToProps)(Invoice);
